import React from 'react';
import Head from "next/head";
import { Layout } from "../components";
import fetch from 'isomorphic-unfetch'

/**
 *
 * @param articles
 * @returns {*}
 * @constructor
 */
const HomePage = (articles) => {
  return (
    <Layout>
      <Head>
        <title>Welcome</title>
      </Head>

      <section className="section">
        <div className="columns is-multiline">
        {Object.keys(articles).map((article, key) => {
          const {
            urlToImage,
            author,
            publishedAt,
            title,
            description
          } = articles[article];

          let shortTitle = title.substr(0, 10);
          shortTitle = shortTitle.substr(0, Math.min(shortTitle.length, shortTitle.lastIndexOf(" ")));

          return (
              <div className={ key === 0 || key === 3 ? 'column is-two-thirds' : 'column'}>
                { key === 0 &&
                  <div className='columns'>
                    <div className='is-half hero hero--custom'>
                      <div className='hero-body'>
                        <div className='container'>
                          <h1 className='title'>
                            {title}
                          </h1>
                          <h2>
                            {description}
                          </h2>
                        </div>
                      </div>
                    </div>
                    <div className='hero--custom'>
                      <img src={urlToImage} alt={title} />
                    </div>
                  </div>
                }
                { key >= 1 &&
                  <div>{description}</div>
                }
              </div>
          )
        })}
        </div>
